tinymce.PluginManager.add('savepage', function(editor, url) {
    editor.addButton('savepage', {
       title: gettext('Save Draft'),
       text: gettext('Save Draft'),
       classes: 'btn-save-page',
       icon: 'icon-save'
    });

    editor.addButton('loaddraft', {
        title: 'Load Draft',
        text: 'Load Draft',
        icon: false,
        classes: 'btn-load-draft',
    });

});
